`pm_dormitory_managers`.`community_id`
= #{communityIds[0]}
IN
#{communityId}
ORDER BY `pm_dormitory_managers`.`dormitory_manager_id`

SELECT `pm_communities`.`name` AS 'community_name', `pm_dormitory_managers`.`dormitory_manager_id`, `pm_dormitory_managers`.`name` AS 'dormitory_manager_name', `pm_dormitory_managers`.`sex`, `pm_dormitory_managers`.`birth`, `pm_dormitory_managers`.`political_status`, `pm_dormitory_managers`.`work_status`, `pm_dormitory_managers`.`education`, `pm_dormitory_managers`.`address`, `pm_dormitory_managers`.`manager_address`, `pm_dormitory_managers`.`manager_count`, `pm_dormitory_managers`.`telephones`, `pm_subcontractors`.`name` AS 'subcontractor_name', `pm_subcontractors`.`telephone` AS 'subcontractor_telephone', `pm_communities`.`subdistrict_id` FROM `pm_dormitory_managers` LEFT JOIN `pm_communities` ON `pm_dormitory_managers`.`community_id` = `pm_communities`.`community_id` LEFT JOIN pm_subcontractors ON `pm_dormitory_managers`.`subcontractor_id` = `pm_subcontractors`.`subcontractor_id`
`pm_communities`.`subdistrict_id` = #{item.roleLocationId}
`pm_communities`.`community_id` = #{item.roleLocationId}

SELECT `pm_dormitory_managers`.`dormitory_manager_id`, `pm_dormitory_managers`.`name` AS 'dormitory_manager_name', CASE `pm_dormitory_managers`.`sex` WHEN 0 THEN '男' WHEN 1 THEN '女' ELSE '未知' END AS 'sex_name', TIMESTAMPDIFF(YEAR, `pm_dormitory_managers`.`birth`, CURDATE()) AS 'age', `pm_dormitory_managers`.`work_status`, `pm_dormitory_managers`.`telephones`, `pm_dormitory_managers`.`community_id`, `pm_communities`.`name` AS 'community_name' FROM `pm_dormitory_managers` LEFT JOIN `pm_communities` ON `pm_dormitory_managers`.`community_id` = `pm_communities`.`community_id`
`pm_dormitory_managers`.`name` LIKE CONCAT('%', #{object.name}, '%')
AND `pm_dormitory_managers`.`sex` = #{object.sex}
AND `pm_dormitory_managers`.`address` LIKE CONCAT('%', #{object.address}, '%')
AND `pm_dormitory_managers`.`telephones` LIKE CONCAT('%', #{object.telephones}, '%')
AND `pm_dormitory_managers`.`community_id` = #{userRoleLocationId}
AND `pm_communities`.`subdistrict_id` = #{userRoleLocationId}
ORDER BY `pm_dormitory_managers`.`dormitory_manager_id`

SELECT
`pm_dormitory_managers`.`name` AS 'dormitory_manager_name',
`pm_dormitory_managers`.`community_id`,
`pm_communities`.`name` AS 'community_name'
FROM `pm_dormitory_managers`
LEFT JOIN `pm_communities` ON `pm_dormitory_managers`.`community_id` = `pm_communities`.`community_id`

    CONCAT(`pm_dormitory_managers`.`name`, `pm_dormitory_managers`.`address`) = #{nameAddress}

        AND `pm_dormitory_managers`.`dormitory_manager_id` != #{id}

    AND `pm_communities`.`subdistrict_id` = #{subdistrictId}

SELECT `pm_dormitory_managers`.`dormitory_manager_id`, `pm_dormitory_managers`.`name` AS 'dormitory_manager_name', `pm_dormitory_managers`.`telephones`, `pm_dormitory_managers`.`community_id`, `pm_communities`.`name` AS 'community_name' FROM `pm_dormitory_managers` LEFT JOIN `pm_communities` ON `pm_dormitory_managers`.`community_id` = `pm_communities`.`community_id`
`pm_dormitory_managers`.`telephones` LIKE CONCAT('%', #{phone}, '%')
AND `pm_dormitory_managers`.`dormitory_manager_id` != #{id}
AND `pm_communities`.`subdistrict_id` = #{subdistrictId}
